      "ScriptBlock": "\n\n        $result = Test-MtLimitOnMicrosoftDomainUsage\n\n        if ($null -ne $result) {\n            $result | Should -Be $true -Because \"no or less then 100 outbound mails are send per day using the .onmicrosoft.com domain.\"\n        }\n    ",
      "ScriptBlockFile": "/Users/merill/GitHub/maester/tests/Maester/Exchange/Test-ExchangeSetting.Tests.ps1",
      "ErrorRecord": [],
      "Block": "Maester/Exchange",
